  Carimbo.*,
  WPessoa_gsRecognize(WPessoa_Id) as Recognize
from
  Carimbo
where
  Carimbo.WPessoa_Id in ( 1600000158739,1600000083694,1610000002122,1600000100123,1600000083448,1610000002169,1610000002079,1600000010619,1610000002127,1610000002116,1600000139177,1610000002362 ) 
and
  Carimbo.Id not in ( 122700000000002,122700000000011,122700000000019 )
order by Recognize